//var d3 = require("./greetings.js");
var config = {
    margin: {top: 30, right: 20, bottom: 30, left: 50},
    width: 375,
    height: 375,
    timeSeries: false,
    el: "body"
};


// data/time parsing
var parseDate = d3.time.format("%d-%b-%y").parse;

// Define the line
var valueline;

// set graph dimensions
var margin = {top: 30, right: 20, bottom: 30, left: 50},
    width = 1200 - margin.left - margin.right,
    height = 400 - margin.top - margin.bottom;

var x;
var y;
var xAxis;
var yAxis;

var lines = [];

function createLineChart(data){

    // set the ranges by graph dimension
    if(config.timeSeries){
        x = d3.time.scale().range([0, width]);
    }else{
        x = d3.scale.linear().range([0, width]);
    }
    
    
    y = d3.scale.linear().range([height, 0]);

    // define graph x/y axis
    xAxis = d3.svg.axis().scale(x)
        .orient("bottom")
    if(config.timeSeries){
        xAxis.ticks(5)
            .tickFormat(d3.time.format("%Y-%m-%d"))
    }else{
        xAxis.ticks(data.length);
    }

    yAxis = d3.svg.axis().scale(y)
        .orient("left").ticks(5);

    valueline = d3.svg.line()
        .x(function(d) { return x(d.xprop); })
        .y(function(d) { return y(d.yprop); });        
        
    // Adds the svg canvas
    var svg = d3.select("body")
        .append("svg")
            .attr("width", width + margin.left + margin.right)
            .attr("height", height + margin.top + margin.bottom)
        .append("g")
            .attr("transform", 
                "translate(" + margin.left + "," + margin.top + ")");

    data.forEach(function(d) {
        if(config.timeSeries){
            d.xprop = parseDate(d.xprop); 
        }
        d.yprop = +d.yprop;
    });

    // Scale the range of the data
    x.domain(d3.extent(data, function(d) { return d.xprop; }));
    y.domain([0, d3.max(data, function(d) { return d.yprop; })]);

    // Add the valueline path.
    svg.append("path")
        .attr("class", "line")
        .attr("d", valueline(data));

    // Add the X Axis
    svg.append("g")
        .attr("class", "x axis")
        .attr("transform", "translate(0," + height + ")")
        .call(xAxis);

    // Add the Y Axis
    svg.append("g")
        .attr("class", "y axis")
        .call(yAxis);
};

